<?php

namespace LotrBundle\Tests\Controller;

use Symfony\Bundle\FrameworkBundle\Test\WebTestCase;

class CharactersControllerTest extends WebTestCase
{
    public function testGetCharacter()
    {
        $client = static::createClient();

        $client->request('GET', '/character/frodon');
        $json = '[{"id":3,"slug":"frodon","name":"Frodon Sacquet","race":{"id":3,"slug":"hobbit","description":"Egalement appel\u00e9s Semi-hommes ou Periannath."}}]';
        $this->assertEquals($json, $client->getResponse()->getContent());
        $this->assertJson($client->getResponse()->getContent());
    }

    public function testgetCharacterTripByDate()
    {
        $client = static::createClient();

        $client->request('GET', '/character/frodon/date/3018-10-09');
        $json = '[{"id":385,"character":{"id":3,"slug":"frodon","name":"Frodon Sacquet","race":{"id":3,"slug":"hobbit","description":"Egalement appel\u00e9s Semi-hommes ou Periannath."}},"date":"3018-10-09T00:00:00+0100","coordx":34,"coordy":24,"status":{"id":2,"slug":"Bless\u00e9"}}]';
        $this->assertEquals($json, $client->getResponse()->getContent());
        $this->assertJson($client->getResponse()->getContent());
    }

