    public function testgetCharacterTripByPlaceAndDate()
    {
        $client = static::createClient();

        $client->request('GET', '/character/frodon/place/moria/date/3019-10-09');
        $json = '[[{"id":5,"slug":"moria","name":"Moria","coordx":41,"coordy":35}],"error : frodon wasn\'t here at this date"]';
        $this->assertEquals($json, $client->getResponse()->getContent());
        $this->assertJson($client->getResponse()->getContent());

        $client->request('GET', '/character/frodon/place/hobbitebourg/date/3018-09-23');
        $json = '[[{"id":1,"slug":"hobbitebourg","name":"Hobbitebourg","coordx":13,"coordy":24}],[{"id":369,"character":{"id":3,"slug":"frodon","name":"Frodon Sacquet","race":{"id":3,"slug":"hobbit","description":"Egalement appel\u00e9s Semi-hommes ou Periannath."}},"date":"3018-09-23T00:00:00+0100","coordx":13,"coordy":24,"status":{"id":1,"slug":"Bonne sant\u00e9"}}]]';
        $this->assertEquals($json, $client->getResponse()->getContent());
        $this->assertJson($client->getResponse()->getContent());
    }

